import React, { useState, useEffect } from 'react';

const Game = () => {
  const [breeds, setBreeds] = useState([]);
  const [imageUrl, setImageUrl] = useState('');
  const [correctBreed, setCorrectBreed] = useState('');
  const [score, setScore] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBreeds();
  }, []);

  const fetchBreeds = async () => {
    try {
      const response = await fetch('/breeds');
      const data = await response.json();
      setBreeds(data.breeds);
      fetchRandomDog();
    } catch (error) {
      console.error('Error fetching breeds:', error);
    }
  };

  const fetchRandomDog = async () => {
    setLoading(true);
    try {
      const response = await fetch('/random_dog');
      const data = await response.json();
      setImageUrl(data.image_url);
      setCorrectBreed(data.breed);
    } catch (error) {
      console.error('Error fetching random dog:', error);
    } finally {
      setLoading(false);
    }
  };

  const checkAnswer = (selectedBreed) => {
    if (selectedBreed === correctBreed) {
      setScore(score + 1);
    } else {
      alert(`Incorrect! The correct breed was ${correctBreed}`);
      setScore(0);
    }
    fetchRandomDog();
  };

  return (
    <div className="p-4">
      {loading ? (
        <div className="flex justify-center items-center">
          <div className="spinner-border animate-spin inline-block w-8 h-8 border-4 rounded-full" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <>
          <div className="mb-4">
            <img src={imageUrl} alt="Random Dog" className="w-full h-auto" />
          </div>
          <div className="grid grid-cols-2 gap-4">
            {breeds.slice(0, 4).map((breed, index) => (
              <button
                key={index}
                className="bg-blue-500 text-white p-2 rounded hover:bg-blue-700 transition duration-200"
                onClick={() => checkAnswer(breed)}
              >
                {breed}
              </button>
            ))}
          </div>
          <p className="mt-4 text-lg">Score: {score}</p>
        </>
      )}
    </div>
  );
};

export default Game;
