import React, { useState, useEffect } from 'react';

const Game = () => {
  const [breeds, setBreeds] = useState([]);
  const [imageUrl, setImageUrl] = useState('');
  const [correctBreed, setCorrectBreed] = useState('');
  const [score, setScore] = useState(0);

  useEffect(() => {
    fetchBreeds();
  }, []);

  const fetchBreeds = async () => {
    const response = await fetch('/breeds');
    const data = await response.json();
    setBreeds(data.breeds);
  };

  const fetchRandomDog = async () => {
    const response = await fetch('/random_dog');
    const data = await response.json();
    setImageUrl(data.image_url);
    setCorrectBreed(data.breed);
  };

  const checkAnswer = (selectedBreed) => {
    if (selectedBreed === correctBreed) {
      setScore(score + 1);
    } else {
      alert(`Incorrect! The correct breed was ${correctBreed}`);
      setScore(0);
    }
    fetchRandomDog();
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <img src={imageUrl} alt="Random Dog" className="w-full h-auto" />
      </div>
      <div className="grid grid-cols-2 gap-4">
        {breeds.slice(0, 4).map((breed, index) => (
          <button
            key={index}
            className="bg-blue-500 text-white p-2 rounded"
            onClick={() => checkAnswer(breed)}
          >
            {breed}
          </button>
        ))}
      </div>
      <p className="mt-4 text-lg">Score: {score}</p>
    </div>
  );
};

export default Game;
